function longestValidParentheses(s: string): number {
  const n = s.length;
  const dp = new Array(n + 1).fill(0);
  let ans = 0;
  for (let i = 1; i < n; i++) {
    const ch = s[i];
    if (ch === '(') dp[i + 1] = 0;
    else if (s[i - 1] === '(') dp[i + 1] = dp[i - 1] + 2;
    else if (s[i - dp[i] - 1] === '(') dp[i + 1] = dp[i] + 2 + dp[i - dp[i] - 1];
    else dp[i + 1] = 0;
    ans = Math.max(ans, dp[i + 1]);
  }
  return ans;
}
