function groupAnagrams(strs: string[]): string[][] {
  const cache: Record<string, string[]> = {};
  const toKey = (str: string) =>
    str
      .split('')
      .sort((a, b) => a.codePointAt(0)! - b.codePointAt(0)!)
      .join('');
  for (const str of strs) {
    const key = toKey(str);
    let arr = cache[key];
    if (!arr) {
      cache[key] = arr = [];
    }
    arr.push(str);
  }
  return Object.values(cache);
}
