/**
 * @param {string} s
 * @return {string}
 */
var longestPalindrome = function (s) {
  const len = s.length;
  let maxRes = '';
  if (len === 0) return maxRes;
  for (let i = 0; i < len; i++) {
    const c = s[i];
    let left = i - 1;
    let right = i + 1;
    let maxS = c;
    while (i < len && c === s[i + 1]) {
      maxS += c;
      right++;
      i++;
    }
    while (left >= 0 && right <= len - 1) {
      if (s[left] !== s[right]) break;
      maxS = s[left] + maxS + s[right];
      left--;
      right++;
    }
    maxRes = maxS.length > maxRes.length ? maxS : maxRes;
  }
  return maxRes;
};
