/**
 * @param {number} n
 * @return {string[][]}
 */
var totalNQueens = function (n) {
  const cols = new Array(n);
  let res = 0;
  queues(0);
  function queues(row) {
    if (row === n) {
      res++;
    }
    for (let col = 0; col < n; col++) {
      if (isValid(row, col)) {
        cols[row] = col;
        queues(row + 1);
      }
    }
  }
  function isValid(row, col) {
    for (let i = 0; i < row; i++) {
      if (cols[i] === col) return false;
      if (row - i === Math.abs(cols[i] - col)) return false;
    }
    return true;
  }
  return res;
};
