/**
 * @param {number} n
 * @return {string[][]}
 */
var totalNQueens = function (n) {
  const cols = new Array(n);
  let res = 0;
  queues(0);
  function queues(row) {
    if (row === n) {
      res++;
    }
    for (let col = 0; col < n; col++) {
      if (isValid(row, col)) {
        cols[row] = col;
        queues(row + 1);
      }
    }
  }
  function isValid(row, col) {
    for (let i = 0; i < row; i++) {
      if (cols[i] === col) return false;
      if (row - i === Math.abs(cols[i] - col)) return false;
    }
    return true;
  }
  return res;
};

function totalNQueens(n: number): number {
  const cols = new Array(n);
  let ans = 0;
  find(0);
  return ans;
  function find(row: number) {
    if (row === n) {
      ans++;
      return;
    }
    for (let i = 0; i < n; i++) {
      if (check(row, i)) {
        cols[row] = i;
        find(row + 1);
      }
    }
  }
  function check(row: number, col: number): boolean {
    for (let i = 0; i < row; i++) {
      if (cols[i] === col) return false;
      if (row - i === Math.abs(cols[i] - col)) return false;
    }
    return true;
  }
}
