function reverse(x: number): number {
  let ans = 0;
  const maxVal = 2 ** 31 - 1;
  const minVal = (-2) ** 31;
  while (x !== 0) {
    ans = ans * 10 + (x % 10);
    x = (x / 10) >> 0;
    if (ans > maxVal || ans < minVal) return 0;
  }
  return ans;
}

function reverse(x: number): number {
  const num = Number(Math.abs(x).toString().split('').reverse().join(''));
  return (x < 0 ? -1 : 1) * (num > 2 ** 31 - 1 || num < (-2) ** 31 ? 0 : num);
}
