function setZeroes(matrix: number[][]): void {
  const rowLen = matrix.length;
  const colLen = matrix[0].length;
  const colIndexSet = new Set<number>();
  for (let i = 0; i < rowLen; i++) {
    const row = matrix[i];
    let f = false;
    for (let j = 0; j < colLen; j++) {
      if (row[j] === 0) {
        f = true;
        colIndexSet.add(j);
      }
    }
    f && row.fill(0);
  }
  for (let i = 0; i < rowLen; i++) {
    for (const j of colIndexSet) {
      matrix[i][j] = 0;
    }
  }
}
