function sortColors(nums: number[]): void {
  const cache: Record<number, number> = {
    0: 0,
    1: 0,
    2: 0,
  };
  for (const num of nums) {
    cache[num]++;
  }
  nums.length = 0;
  nums.push(
    ...new Array(cache[0]).fill(0),
    ...new Array(cache[1]).fill(1),
    ...new Array(cache[2]).fill(2)
  );
}
