class Solution {
   public:
    int s2i(string str) {
        int ans = 0, f = 1;
        for (int i = 0; i < str.size(); i++) {
            if (i == 0 && str[i] == '-') {
                f = -1;
                continue;
            }
            ans = ans * 10 + str[i] - '0';
        }
        return ans * f;
    }
    int evalRPN(vector<string>& tokens) {
        stack<int> s;
        for (auto& str : tokens) {
            if (str == "+" || str == "-" || str == "*" || str == "/") {
                int num1 = s.top();
                s.pop();
                int num2 = s.top();
                s.pop();
                int ans;
                if (str == "+")
                    ans = num2 + num1;
                else if (str == "-")
                    ans = num2 - num1;
                else if (str == "*")
                    ans = num2 * num1;
                else
                    ans = num2 / num1;
                s.push(ans);
            } else {
                s.push(s2i(str));
            }
        }
        return s.top();
    }
};
