var treeToDoublyList = function (root) {
  if (root === null) return null;
  let head, pre;
  inorder(root);
  head.left = pre;
  pre.right = head;
  return head;
  function inorder(node) {
    if (!node) return;
    inorder(node.left);
    if (!pre) {
      head = node;
    } else {
      pre.right = node;
    }
    node.left = pre;
    pre = node;
    inorder(node.right);
  }
};
