function divisorGame(N: number): boolean {
  const dp = new Array(N).fill(false);
  for (let i = 2; i <= N; i++) dp[i - 1] = getMods(i).some(num => !dp[i - num - 1]);
  return dp[N - 1];
  function getMods(num: number) {
    const arr = [1];
    for (let i = 2; i < num; i++) {
      if (num % i === 0) arr.push(i);
    }
    return arr;
  }
}
