function getRow(rowIndex: number): number[] {
  const cache: number[][] = [[1], [1, 1]];
  for (let i = 2; i <= 33; i++) {
    const arr = [1];
    const prev = cache[i - 1];
    for (let j = 0, l = prev.length; j < l - 1; j++) {
      arr.push(prev[j] + prev[j + 1]);
    }
    arr.push(1);
    cache.push(arr);
  }
  return cache[rowIndex];
}

