class Solution {
   public:
    int maxProfit(vector<int> &prices) {
        int nmin = prices[0], ans = 0;
        for (int i = 1; i < prices.size(); i++) {
            ans = max(ans, prices[i] - nmin);
            nmin = min(nmin, prices[i]);
        }
        return ans;
    }
};
