function singleNumber(nums: number[]): number {
  const len = nums.length;
  nums.sort((a, b) => a - b);
  let i = 0;
  while (i < len - 1) {
    if (nums[i] === nums[i + 1]) i += 3;
    else break;
  }
  return nums[i];
}

function singleNumber(nums: number[]): number {
  return [
    ...nums
      .reduce((map, v) => {
        map.set(v, (map.get(v) ?? 0) + 1);
        return map;
      }, new Map<number, number>())
      .entries(),
  ].filter(([, v]) => v === 1)[0][0];
}
