function wordBreak(s: string, wordDict: string[]): boolean {
  const len = s.length;
  const wordSet = new Set(wordDict);
  const dp = [true];
  for (let i = 1; i <= len; i++)
    for (let j = 0; j < i; j++)
      if (dp[j] && wordSet.has(s.substr(j, i - j))) {
        dp[i] = true;
        break;
      }
  return dp[len] ? dp[len] : false;
}
