function reorderList(head: ListNode | null): void {
  if (head === null) return;
  const queue: ListNode[] = [];
  let temp: ListNode | null = head;
  while (temp !== null) {
    queue.push(temp);
    temp = temp.next;
  }
  queue.shift();
  let tag = true;
  temp = head;
  while (queue.length !== 0) {
    temp.next = (tag = !tag) ? queue.shift()! : queue.pop()!;
    temp = temp!.next;
  }
  temp.next = null;
}
