function insertionSortList(head: ListNode | null): ListNode | null {
  if (head === null) return null;
  const tempHead = new ListNode(0, head);
  let last = head;
  let p = head.next;
  last.next = null;
  while (p !== null) {
    const next = p.next;
    add(p);
    p = next;
  }
  return tempHead.next;
  function add(node: ListNode): void {
    let temp = tempHead;
    const val = node.val;
    while (temp.next !== null && temp.next !== last) {
      if (temp.next.val < val) {
        temp = temp.next;
      } else {
        node.next = temp.next;
        temp.next = node;
        return;
      }
    }
    if (temp.next === last) {
      if (last.val > val) {
        node.next = temp.next;
        temp.next = node;
      } else {
        last.next = node;
        node.next = null;
        last = node;
      }
    }
  }
}
