function findMin(nums: number[]): number {
  const len = nums.length;
  for (let i = 1; i < len; i++) {
    if (nums[i] < nums[i - 1]) return nums[i];
  }
  return nums[0];
}

function findMin(nums: number[]): number {
  return Math.min.apply({}, nums);
}

function findMin(nums: number[]): number {
  let left = 0;
  let right = nums.length - 1;
  while (left < right) {
    const mid = ~~((left + right) / 2);
    if (nums[mid] < nums[right]) right = mid;
    else left = mid + 1;
  }
  return nums[left];
}
