var trailingZeroes = function (n) {
  let ans = 0;
  let m = 5;
  while (n / m) {
    ans += ~~(n / m);
    m *= 5;
  }
  return ans;
};

class Solution {
   public:
    int trailingZeroes(int n) {
        int ans = 0;
        for (int cnt = 1; pow(5, cnt) <= n; cnt++) ans += n / pow(5, cnt);
        return ans;
    }
};
