class Solution {
   public:
    vector<int> distributeCandies(int candies, int num_people) {
        vector<int> ans(num_people, 0);
        for (int i = 0, cnt = 1; candies > 0; i = (i + 1) % num_people, cnt++) {
            ans[i] += min(candies, cnt);
            candies -= cnt;
        }
        return ans;
    }
};
