function longestWPI(hours: number[]): number {
  hours = hours.map(v => (v > 8 ? 1 : -1));
  const len = hours.length;
  const sums = [0];
  let sum = 0;
  hours.forEach(hour => sums.push((sum += hour)));
  let max = 0;
  for (let i = 1; i < len + 1; i++) {
    for (let j = 0; j < i; j++) {
      if (sums[i] - sums[j] > 0) max = Math.max(max, i - j);
    }
  }
  return max;
}
