function numRollsToTarget(d: number, f: number, target: number): number {
  const mod = 1e9 + 7;
  const dp = new Array(d + 1).fill(0).map(_ => new Array(target + 1).fill(0));
  dp[0][0] = 1;
  for (let i = 1; i <= d; i++) {
    for (let j = i; j <= target; j++) {
      for (let k = 1; k <= f && j >= k; k++) {
        dp[i][j] = (dp[i][j] + dp[i - 1][j - k]) % mod;
      }
    }
  }
  return dp[d][target];
}
