class Solution {
   public:
    int countCharacters(vector<string> &words, string chars) {
        int list[26] = {0}, ans = 0, tmp[26] = {0};
        for (auto &ch : chars) list[ch - 'a']++;
        for (auto &word : words) {
            memset(tmp, 0, sizeof(int) * 26);
            int f = 1;
            for (auto &ch : word) tmp[ch - 'a']++;
            for (int i = 0; i < 26; i++) {
                if (list[i] < tmp[i]) {
                    f = 0;
                    break;
                }
            }
            if (f) ans += word.size();
        }
        return ans;
    }
};
