function reverseParentheses(s: string): string {
  const stack: string[] = [];
  for (const c of s) {
    if (c === ')') {
      let str = '';
      while (stack[stack.length - 1] !== '(') str = stack.pop()! + str;
      stack.pop();
      stack.push(str.split('').reverse().join(''));
    } else stack.push(c);
  }
  return stack.join('');
}
