class Solution {
   public:
    int minCostToMoveChips(vector<int>& position) {
        int ans1 = 0, ans2 = 0;
        for (auto& num : position) {
            if (num & 1)
                ans1++;
            else
                ans2++;
        }
        return min(ans1, ans2);
    }
};
