function minRemoveToMakeValid(s: string): string {
  let fCount = 0;
  let ans = '';
  for (const c of s) {
    if (c === '(') {
      fCount++;
      ans += c;
    } else if (c === ')') {
      if (fCount !== 0) {
        ans += c;
        fCount--;
      }
    } else {
      ans += c;
    }
  }
  const temp = ans;
  fCount = 0;
  ans = '';
  for (let i = temp.length - 1; i >= 0; i--) {
    const c = temp[i];
    if (c === ')') {
      fCount++;
      ans = c + ans;
    } else if (c === '(') {
      if (fCount !== 0) {
        ans = c + ans;
        fCount--;
      }
    } else {
      ans = c + ans;
    }
  }
  return ans;
}
