class Solution {
public:
    int gcd(int a, int b) {
        if (!b) return a;
        if (a < b) return gcd(b, a);
        return gcd(b, a % b);
    }
    bool isGoodArray(vector<int>& nums) {
        int res = nums[0];
        for (auto &num : nums) {
            res = gcd(res, num);
            if (res == 1) break;
        }
        return res == 1;
    }
};

class Solution:
    def isGoodArray(self, nums: List[int]) -> bool:
        def gcd(a, b):
            if not b:
                return a
            if a < b:
                return gcd(b, a)
            return gcd(b, a % b)
        res = nums[0]
        for num in nums:
            res = gcd(res, num)
            if res == 1:
                break
        return res == 1

fn gcd(a: i32, b: i32) -> i32 {
    if b == 0 {
        a
    } else if a < b {
        gcd(b, a)
    } else {
        gcd(b, a % b)
    }
}

impl Solution {
    pub fn is_good_array(nums: Vec<i32>) -> bool {
        let mut res = nums[0];
        for num in nums {
            res = gcd(res, num);
            if res == 1 {
                break;
            }
        }
        res == 1
    }
}
