class FindElements:
    def __init__(self, root: Optional[TreeNode]):
        self.set = set()
        def dfs(node: Optional[TreeNode], cur: int):
            if not node: return
            self.set.add(cur)
            dfs(node.left, cur * 2 + 1)
            dfs(node.right, cur * 2 + 2)
        dfs(root, 0)
    def find(self, target: int) -> bool:
        return target in self.set
