class Solution {
public:
    int subtractProductAndSum(int n) {
        int num1 = 1, num2 = 0;
        while (n) {
            num1 *= n % 10;
            num2 += n % 10;
            n /= 10;
        }
        return num1 - num2;
    }
};

class Solution:
    def subtractProductAndSum(self, n: int) -> int:
        num1 = 1
        num2 = 0
        while n:
            num1 *= n % 10
            num2 += n % 10
            n //= 10
        return num1 - num2

impl Solution {
    pub fn subtract_product_and_sum(mut n: i32) -> i32 {
        let mut num1 = 1;
        let mut num2 = 0;
        while n != 0 {
            num1 *= n % 10;
            num2 += n % 10;
            n /= 10;
        }
        num1 - num2
    }
}
