function sortByBits(arr: number[]): number[] {
  const count = (num: number): number =>
    num
      .toString(2)
      .split('')
      .reduce((total, cur) => total + Number(cur), 0);
  return arr.sort((a, b) => {
    const c1 = count(a);
    const c2 = count(b);
    return c1 === c2 ? a - b : c1 - c2;
  });
}

function sortByBits(arr: number[]): number[] {
  const count = (num: number): number => {
    let ans = 0;
    while (num) {
      num &= num - 1;
      ans++;
    }
    return ans;
  };
  return arr.sort((a, b) => {
    const c1 = count(a);
    const c2 = count(b);
    return c1 === c2 ? a - b : c1 - c2;
  });
}
