function smallerNumbersThanCurrent(nums: number[]): number[] {
  const ans: number[] = [];
  for (let i = 0, l = nums.length; i < l; i++) {
    ans[i] = count(i);
  }
  return ans;
  function count(i: number) {
    const num = nums[i];
    let c = 0;
    nums.forEach(v => {
      if (v < num) c++;
    });
    return c;
  }
}
