class Solution {
        public:
         string generateTheString(int n) {
             string ans = "";
             if ((n & 1) == 0) {
                 ans += 'b';
                 n--;
             }
             for (int i = 0; i < n; i++) ans += 'a';
             return ans;
         }
     };

impl Solution {
    pub fn generate_the_string(n: i32) -> String {
        let mut n = n;
        let mut ans = String::new();
        if n & 1 == 0 {
            ans.push('b');
            n -= 1;
        }
        ans.push_str(&"a".repeat(n as usize));
        ans
    }
}
