impl Solution {
    pub fn min_subsequence(nums: Vec<i32>) -> Vec<i32> {
        let mut nums = nums;
        nums.sort();
        let sum: i32 = nums.iter().sum();
        let mut cur = 0;
        let mut i = nums.len() - 1;
        let mut ans = Vec::new();
        while cur * 2 <= sum {
            cur += nums[i];
            ans.push(nums[i]);
            i -= 1;
        }
        ans
    }
}
