function stringMatching(words: string[]): string[] {
  words.sort((a, b) => a.length - b.length);
  const set = new Set<string>();
  for (let i = 0; i < words.length; i++) {
    for (let j = 0; j < i; j++) {
      if (words[i].includes(words[j])) set.add(words[j]);
    }
  }
  return [...set];
}
