class Solution {
public:
    int maxScore(string s) {
        int ans = 0, cnt0 = 0, cnt1 = 0;
        for (auto &c : s) {
            if (c == '1') cnt1++;
        }
        for (int i = 0; i < s.size() - 1; i++) {
            char c = s[i];
            if (c == '0') cnt0++;
            else cnt1--;
            ans = max(ans, cnt0 + cnt1);
        }
        return ans;
    }
};
