function maxScore(cardPoints: number[], k: number): number {
  const len = cardPoints.length;
  let rightNum = 0;
  let num = cardPoints.slice(0, k).reduce((total, cur) => total + cur, 0);
  let max = num;
  while (rightNum < k) {
    max = Math.max(
      max,
      (num = num - cardPoints[k - rightNum - 1] + cardPoints[len - rightNum - 1])
    );
    rightNum++;
  }
  return max;
}
