class Solution {
public:
    vector<string> buildArray(vector<int>& target, int n) {
        vector<string> list;
        int cur = 1;
        for (auto &item : target) {
            while (cur < item) {
                list.push_back("Push");
                list.push_back("Pop");
                cur++;
            }
            list.push_back("Push");
            cur++;
        }
        return list;
    }
};
