function maxPower(s: string): number {
  let ans = 0;
  for (let i = 0, n = s.length; i < n; i++) {
    let cnt = 1;
    const ch = s[i];
    while (i + 1 < n && s[i + 1] === ch) {
      i++;
      cnt++;
    }
    ans = Math.max(ans, cnt);
  }
  return ans;
}

int maxPower(char * s){
    int ans = 0;
    for (int i = 0; i < strlen(s); i++) {
        int cnt = 1;
        char ch = s[i];
        while (i + 1 < strlen(s) && s[i + 1] == ch) {
            i++;
            cnt++;
        }
        if (cnt > ans) ans = cnt;
    }
    return ans;
}
