class Solution:
    def minReorder(self, n: int, connections: List[List[int]]) -> int:
        froms = [[] for _ in range(n)]
        tos = [[] for _ in range(n)]
        for a, b in connections:
            froms[a].append(b)
            tos[b].append(a)
        q = deque()
        q.append((0, 0, -1))
        ans = 0
        while q:
            cur, f, p = q.popleft()
            ans += len(froms[cur]) - f
            for next in froms[cur]:
                if next != p: q.append((next, 0, cur))
            for next in tos[cur]:
                if next != p: q.append((next, 1, cur))
        return ans
