function numWaterBottles(numBottles: number, numExchange: number): number {
  let ans = numBottles;
  while (numBottles >= numExchange) {
    const bottles = Math.floor(numBottles / numExchange);
    ans += bottles;
    numBottles = bottles + (numBottles % numExchange);
  }
  return ans;
}
