class Solution:
    def longestAwesome(self, s: str) -> int:
        n = len(s)
        res = 0
        cur = 0
        map = { 0: -1 }
        for i in range(n):
            num = ord(s[i]) - ord('0')
            cur ^= 1 << num
            for offset in range(10):
                v = cur ^ (1 << offset)
                if v in map:
                    res = max(res, i - map[v])
            if cur in map:
                res = max(res, i - map[cur])
            else:
                map[cur] = i
        return res
