class Solution {
   public:
    string modifyString(string s) {
        for (int i = 0, n = s.size(); i < n; i++) {
            if (s[i] != '?') continue;
            char prev = i > 0 ? s[i - 1] : '\0',
                 next = i < n - 1 ? s[i + 1] : '\0';
            for (char ch = 'a'; ch <= 'z'; ch++) {
                if (prev != ch && next != ch) {
                    s[i] = ch;
                    break;
                }
            }
        }
        return s;
    }
};
