function sumOddLengthSubarrays(arr: number[]): number {
  const n = arr.length;
  const sums = [0];
  arr.forEach(v => sums.push(v + sums[sums.length - 1]));
  let ans = 0;
  for (let l = 0; l <= n; l++) {
    for (let r = l + 1; r <= n; r += 2) {
      ans += sums[r] - sums[l];
    }
  }
  return ans;
}
