class ParkingSystem {
  private parkingLot: number[];
  constructor(big: number, medium: number, small: number) {
    this.parkingLot = [big, medium, small];
  }
  addCar(carType: number): boolean {
    if (this.parkingLot[carType - 1] > 0) {
      this.parkingLot[carType - 1]--;
      return true;
    } else return false;
  }
}
