class Solution {
public:
    int specialArray(vector<int>& nums) {
        sort(nums.begin(), nums.end());
        int n = nums.size(), cnt = n;
        for (int i = 0; i < n; i++, cnt--) {
            if (nums[i] >= cnt && (i > 0 && cnt > nums[i - 1] || i == 0)) return cnt;
        }
        return -1;
    }
};
