#define CNT 26
      class Solution {
public:
    int maxLengthBetweenEqualCharacters(string s) {
        int list[CNT], ans = -1;
        memset(list, -1, sizeof(int) * 26);
        for (int i = 0; i < s.size(); i++) {
            if (list[s[i] - 'a'] != -1) ans = max(ans, i - list[s[i] - 'a'] + 1 - 2);
            else list[s[i] - 'a'] = i;
        }
        return ans;
    }
};
