class Solution {
   public:
    char slowestKey(vector<int>& releaseTimes, string keysPressed) {
        int pre = 0, max_v = 0;
        char max_c;
        for (int i = 0; i < releaseTimes.size(); i++) {
            int time = releaseTimes[i] - pre;
            if (time > max_v || time == max_v && keysPressed[i] > max_c) {
                max_v = time;
                max_c = keysPressed[i];
            }
            pre = releaseTimes[i];
        }
        return max_c;
    }
};
