class Solution:
    def maxResult(self, nums: List[int], k: int) -> int:
        n = len(nums)
        dp = [0] * n
        q = deque()
        for i in range(n):
            dp[i] += nums[i]
            while q and q[0] < i - k: q.popleft()
            if q: dp[i] += dp[q[0]]
            while q and dp[q[-1]] <= dp[i]: q.pop()
            q.append(i)
            # print(f'i = {i}, q = {q}, dp = {dp}')
        return dp[-1]
