class Solution {
   public:
    int countGoodRectangles(vector<vector<int>>& rectangles) {
        int len = INT_MIN, ans = 0;
        for (auto& data : rectangles) len = max(len, min(data[0], data[1]));
        for (auto& data : rectangles)
            if (len <= data[0] && len <= data[1]) ans++;
        return ans;
    }
};
