class Solution:
    def tupleSameProduct(self, nums: List[int]) -> int:
        n = len(nums)
        map = Counter()
        for i in range(n):
            for j in range(i + 1, n):
                map[nums[i] * nums[j]] += 1
        return sum(v * (v - 1) * 4 for v in map.values())
